Ext.define('RolandKioskPrint.view.OrderVerifyViewModel', {
    extend: 'Ext.app.ViewModel',
    alias: 'viewmodel.orderverify',

    requires: [
        'Ext.app.bind.Formula'
    ],

    formulas: {
        quantityValue: function(get) {
            return get('quantity') + ' ' + RolandKioskPrint.app.getString('label.designer.pcsLabel')

        },
        priceValue: function(get) {
            if (get('goodsCode') == RolandKioskPrint.app.getString('label.generic.customName')) {
                return RolandKioskPrint.app.getString('label.generic.customPrice')

            }

            var totalPrice = get('quantity') * get('pricePerUnit')

            return get('currency') + ' ' + RolandKioskPrint.AppData.formatNumber(totalPrice)

        }
    }

})
