Ext.define('RolandKioskPrint.view.ProductModelDesignerPreviewViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.productmodeldesignerpreview',

    onCloseButtonClick: function(button, e, eOpts) {
        this.redirectTo('#template/main', true)

    },

    onMainViewBeforeShow: function(component, eOpts) {
        var mode = RolandKioskPrint.AppData.productModelDesignerMode

        var productModelData = RolandKioskPrint.AppData.productModelData


        var viewData = {
            designerMode: mode,
            modelName: productModelData.modelName
        }


        this.getViewModel().setData(viewData)
 // Set the view model data.

        var imgProductModelDesign = this.getView().down('#imgProductModelDesign')

        var url = "url('" + RolandKioskPrint.AppData.getURLWithNonce(productModelData.modelThumbPath) + "')"


        imgProductModelDesign.setStyle({
            backgroundImage: url,
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'contain',
            backgroundPosition: 'center center'
        })

    }

})

