Ext.define('RolandKioskPrint.view.window.HelpWindowViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.window.helpwindow',

    statics: {
        showHelp: function(srcUrl, cssClass, windowWidth) {
            if(RolandKioskPrint.AppData.getHelpWindow() !== null)
            return


            var widget = Ext.widget({
                xtype: 'window.helpwindow',
                componentCls: cssClass,
                width: windowWidth,
                items: [
                {
                    xtype: 'component',
                    autoEl: {
                        tag: 'iframe',
                        src: srcUrl,
                        frameborder: 0
                    }
                }
                ]
            })

            widget.show()


            RolandKioskPrint.AppData.setHelpWindow(widget)

        },

        closeHelp: function() {
            var helpWindow = RolandKioskPrint.AppData.getHelpWindow()

            if(helpWindow !== null){
                helpWindow.destroy()

                RolandKioskPrint.AppData.setHelpWindow(null)

            }
        }
    },

    onWindowBeforeClose: function(panel, eOpts) {
        RolandKioskPrint.AppData.setHelpWindow(null)

    }

})

